mapper.select:
UPDATE board
SET board_hit_cnt = board_hit_cnt + 1
WHERE board_idx = #{boardIdx}

mapper.update:
UPDATE board
SET board_like_cnt = board_like_cnt - 1
WHERE board_idx = #{boardIdx}

UPDATE board
SET board_deleted_yn = 'Y'
WHERE board_idx = #{boardIdx}

mapper.select:
SELECT COUNT(comment_board_idx)
FROM comment
WHERE comment_board_idx = #{commentBoardIdx}
